mapper.select:
SELECT t2.id, t2.title, t4.name typeLabel, t3.nickname publisherName, t5.name levelLabel, t2.publish_time, t1.is_read FROM sys_user_notice t1 INNER JOIN sys_notice ON t2.notice_id = t1.id AND t2.publish_status = 1 LEFT JOIN sys_user t3 ON t2.publisher_id = t3.id LEFT JOIN sys_dict_item t4 ON t2.type = t4.value AND t4.dict_code = 'notice_type' LEFT JOIN sys_dict_item t5 ON t2.level = t5.value AND t5.dict_code = 'notice_level' WHERE t1.user_id = #{queryParams.userId}
AND t2.title LIKE CONCAT('%',#{queryParams.title},'%')
ORDER BY t2.release_time DESC
